# Function to remove HTML tags + extra spaces
def clean_html(text):
    if not isinstance(text, str):
        return text
    text = re.sub(r'<[^>]+>', '', text)  # remove HTML tags
    text = text.replace('\\"', '')       # remove escaped quotes
    text = text.replace('"', '')         # remove extra quotes
    return text.strip()

